

import requests
from bs4 import BeautifulSoup

URL = "https://forecast.weather.gov/MapClick.php?lon=-121.6688576341901&lat=45.34194381514135#.WDRQ3dg76f0"
page = requests.get(URL)

soup = BeautifulSoup(page.content,'html.parser')
snow = soup.find(id="current_conditions-summary")
farenheit = snow.find("p", class_="myforecast-current-lrg")
print("<<<>>>")
print(farenheit.text)
print("---------------")
